import React, { Component } from 'react';
import './Hex.scss';

class HexGrid extends Component {

    hexGrid(h, w) {
        
        let grid = []
        let color=0xf5c4de;
        let rgb = [80,130,255]
        let down = false;
        for(let i=0; i<h; i++){
            let row = []
            for (let j=0; j<w; j++){
                color-=32;
                if(rgb[2]===255) down=true
                if(rgb[2]===150) down=false
                rgb[2]+= down ? -1 : 1;
                row.push(
                    <li>
                        <div className="hexagon" style={{"background":"rgb("+rgb.join(',')+")"}}></div>
                    </li>
                )
                console.log({"background":"rgb("+rgb.join(',')+")"})
            }
            grid.push(
                <div className="row" id={"row_"+i}>
                    {row}
                </div>
            )
        }
        return grid
    }
    render() {
        return (
            <ul id="grid" className="clear">
                {this.hexGrid(30,40)}
            </ul>
        );
    }
} 
export default HexGrid;
