import React, { Component } from 'react';
import './Hex.scss';

class HexGrid extends Component {

    hexGrid(h, w) {
        let grid = []
        for(let i=0; i<h; i++){
            let row = []
            for (let j=0; j<w; j++){
                row.push(
                    <li>
                        <div class="hexagon"></div>
                    </li>
                )
            }
            grid.push(
                <div id={"row_"+i}>
                    {row}
                </div>
            )
        }
    }
    render() {
        return (
            <ul id="grid" class="clear">
                {this.hexGrid(3, 4)}
            </ul>
        );
    }
} 
export default HexGrid;
