import React, { Component } from 'react';
import './Hex.scss';

class HexGrid extends Component {

    hexGrid(h, w) {
        
        let grid = []
        // let color=0xffc170;//0xf5c4de;
        let rgb = [0,175,175]
        let down = false;
        for(let i=0; i<h; i++){
            let row = []
            for (let j=0; j<w; j++){
                
                if(rgb[2]===254) down=true
                if(rgb[2]===190) down=false
                rgb[2]+= down ? -4 : 4;
                rgb[1]+= down ? -4 : 4;

                let color = Math.random()+(0.1*j)>.85 ? "white" : "rgb("+rgb.join(',')+")"
                row.push(
                    <li>
                        <div className="hexagon" style={{"background": color}}></div>
                    </li>
                )
                console.log({"background":"rgb("+rgb.join(',')+")"})
            }
            grid.push(
                <div className="row" id={"row_"+i}>
                    {row}
                </div>
            )
        }
        return grid
    }
    render() {
        return (
            <ul id="grid" className="clear">
                {this.hexGrid(15,20)}
            </ul>
        );
    }
} 
export default HexGrid;
