import React, { Component } from 'react';
import { UncontrolledTooltip } from 'reactstrap'
import { Collapse, Button, CardBody, Container, Row, Col } from 'reactstrap'

import { FaMapMarkerAlt as Location, FaRegCalendarAlt as Date, FaChevronCircleRight as Closed, FaFilePdf as Resume } from 'react-icons/fa'
import { FaPython as Python, FaJava as Java, FaJsSquare as JS, FaHtml5 as HTML} from 'react-icons/fa'
import { FaGitSquare as Git, FaMicrosoft as Windows, FaLinux as Linux, FaCss3Alt as CSS} from 'react-icons/fa'
import { GoTerminal as Bash } from 'react-icons/go'
// import { FaEye as Cpp, FaEye as Unity, FaEye as Csharp } from 'react-icons/fa'
import { CustomCpp as Cpp, CustomUnity as Unity, CustomCsharp as Csharp } from 'react-icons/custom'
import resume from '../resources/Resume.pdf'

import './Experience.css'

import csu from '../resources/csu.svg'
// import cpp from '../resources/C++.svg'
// import csharp from '../resources/csharp.svg'
import latlon from '../resources/lat-lon.png'
import hpe from '../resources/hpe.svg'
import pelco from '../resources/pelco.png'
// import resume from '../resources/Resume.pdf'
import acmw from '../resources/acmw.png'
import eyh from '../resources/eyh.png'
import extralife from '../resources/extralife.png'
import mentor from '../resources/cs[u].png'
// import unity from '../resources/unity.svg'

class Experience extends Component {
  constructor(props){
    super(props);
    this.state = {
      languages: false,
      jobs: false,
      extra: false
    }
    this.toggle = this.toggle.bind(this);
  }

  toggle(block){
    let state = this.state;
    state[block] = !state[block];
    this.setState(state);
  }

  experienceBlock(job) {
    //company, role, location, link, img, start, end, description) {
    let separator = <React.Fragment>&nbsp;&nbsp;&nbsp;&nbsp;</React.Fragment>

    return(
      <div className="experience_block">
        <div className = "job_details">
          <div>
            <p className="lead">{job.company} - {job.role}</p>
            <p><Location/>&nbsp;{job.location}{separator}<Date/>&nbsp;{job.start} - {job.end}</p>
          </div>
          <div className="spacer"/>
          <div>
            <a href={job.link} target="_blank" rel="noopener noreferrer">
              <img id="image" width='150' height= '70' src={job.img} alt={job.company + " logo"}/>
            </a>
          </div>
        </div>
        <p>{job.description} {job.languages}</p>
      </div>
    )
  }

  softwareBadge(name, icon, placement){
    return (
      <div className="badge">
          {icon}
          <div className="label">{name}</div>
      </div>
    );
  }

  
  cutOutHeader(text, toggle) {
    return(
      <div className="sectionHeader" onClick={()=> toggle!=="resume" && this.toggle(toggle)}>
        <svg height="50" className="cutOut">
          <defs>
            <mask id={text.replace(/ /g, "_")} x="0" y="0" width="100%" height="100%">
              <rect x="0" y="0" width="100%" height="100%" fill="#fff"/>
              <text text-anchor="start" x="60px" y="70%" dy="1">{text}</text>
              <g className={"show " + (this.state[toggle] ? "open" : "")}>
                {toggle!=="resume" ? <Closed x="-15px" y="-15px" /> : <Resume x="-15px" y="-15px"/>}
              </g>
            </mask>
          </defs>
          <rect x="0" y="0" width="100%" height="100%" mask={"url(#"+text.replace(/ /g, "_")+")"} />    
        </svg>
      </div>
    );
  }

  render() {
    let badges = 
      <div className="badges">
        {this.softwareBadge("Java", <Java id="Java"/>, "top")}
        {this.softwareBadge("Python", <Python id="Python"/>, "top")}
        {this.softwareBadge("C++", <Cpp id="Cpp"/>, "top")}
        {this.softwareBadge("C#", <Csharp id="Csharp"/>, "top")}
        <br/>
        {this.softwareBadge("Javascript", <JS id="Javascript"/>, "top")}
        {this.softwareBadge("HTML", <HTML id="HTML5"/>, "top")}
        {this.softwareBadge("CSS", <CSS id="CSS"/>, "top")}
        <br/>
        {this.softwareBadge("Unity", <Unity id="Unity"/>, "top")}
        {this.softwareBadge("Windows", <Windows id="Windows"/>, "top")}
        {this.softwareBadge("Linux", <Linux id="Linux"/>, "top")}
        {this.softwareBadge("Shell Script", <Bash id="ShellScript"/>, "top")}
        {this.softwareBadge("Git", <Git id="Git"/>, "top")}
      </div>

    return (
      
      // <div className="scrollable">
      <Container fluid className='page'>
        <div className="heading">
          <div className="name">
            Experience
          </div>
        </div>
        {this.cutOutHeader("Languages and Software", "languages")}
        <div className= {"hidden " + (this.state.languages && "open")} >
          {badges}
        </div>
        
        {this.cutOutHeader("Professional Experience", "jobs")}
        <div className= {"hidden " + (this.state.jobs && "open")} >
          {this.experienceBlock(
              {
                'company' : "Hewlett Packard Enterprise", 
                'role' : "Software Development Intern",
                'location' : "Fort Collins, Colorado", 
                'link' : "www.hpe.com/‎",
                'img' : hpe,
                'start' : "May 2019",
                'end' : "Present",
                'description' : "Saved hours of developer time during triage by creating a tool for the visualization and navigation of log files.\
                Parsed log information from 100,000+ line log files, stored and accessed data using PostgreSQL and GraphQL.\
                Intuitively displayed data in a locally hosted ReactJS web application; log entries follow the chronological flow of system activity and allowing users to filter for more relevant events."
              }
            )}
            {this.experienceBlock(
              {
                'company' : "Pelco by Schneider Electric", 
                'role' : "Intern",
                'location' : "Fort Collins, Colorado", 
                'link' : "https://www.pelco.com/",
                'img' : pelco,
                'start' : "May 2018",
                'end' : "October 2018",
                'description' : "Worked in an agile environment with a remote team to automate testing of API calls to a security video management system using Python and Robot Framework."
              }
            )}
            {this.experienceBlock(
              {
                'company' : "Colorado State University", 
                'role' : "Teaching Assistant", 
