import React, { Component } from "react";
import "../App.scss";

import { Nav, NavItem, NavLink } from 'reactstrap';
import { Container, Row, Col } from 'reactstrap';

import { GoMarkGithub as Github } from 'react-icons/go'
import { FaLinkedin as LinkedIn } from 'react-icons/fa'
import { IoMdMail as Mail } from 'react-icons/io'


import HomePage from "./pages/homePage";
import Home from '../Components/Home.jsx'
import Experience from '../Components/Experience.jsx'
import Projects from '../Components/Projects.jsx'
import Game from '../Components/Game/Game.js'
import Trip from '../Components/Trip-Planner/components/App.js'
import { Switch, Route, withRouter, Link } from "react-router-dom";
import AboutPage from "./pages/aboutPage";

import { TransitionGroup, CSSTransition } from "react-transition-group";

import "./pageTransitions/slideTransition.scss";

class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    prevPage: this.getPage(this.props.location)
  };
}

componentWillReceiveProps() {
  this.setState({ prevPage: this.getPage(this.props.location) });
}

getPage(location) {
  let pages= {
    "experience": 2,
    "projects": 3
  }
  let pathArr = location.pathname.split("/");
  pathArr = pathArr.filter(n => n !== "");
  let pageNumber = pages[pathArr[pathArr.length-1]]
  return pageNumber===undefined ? 0 : pageNumber;
}
navBar(){
  // if(window.location.pathname==="/" + basename + "#/game" || window.location.pathname==="/" + basename + "#/trip-planner") {
  //   return("");
  // }
  var basename = ""
  return(
    <React.Fragment>
    <header className="backgroundColor header">
      <Row>
        <Col xs='8'>
          <Link to={"/"}>ABIGAIL RICTOR</Link>
          <Link to={"/experience"}>EXPERIENCE</Link>
          <Link to={"/projects"}>PROJECTS</Link>
        </Col>
        <Col>
          <Nav className='float-right'>
            <NavItem>
              <NavLink href="http://github.com/amrictor"><Github/></NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="http://www.linkedin.com/in/abigail-rictor-528451110/"><LinkedIn/></NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="mailto:amrictor@gmail.com"><Mail/></NavLink>
            </NavItem>
          </Nav>
        </Col>
      </Row>
    </header>
    <div className='accentBackground'>
    <Container className='pad'>
    </Container>
  </div>
  </React.Fragment> 
  )
}
  render() {
    const { location } = window;

    const currentKey = location.pathname.split("/")[1] || "/";
    const timeout = { enter: 800, exit: 400 };
    console.log( this.getPage(location) , this.state.prevPage )
    return (
      <TransitionGroup component="div" className="App">
        {this.navBar()}
        <CSSTransition
          key={currentKey}
          timeout={timeout}
          classNames="pageSlider"
          mountOnEnter={false}
          unmountOnExit={true}
        >
          <div
            className={
              this.getPage(location) > this.state.prevPage 
                ? "left"
                : "right"
            }
          >
            <Switch location={location}>
              <Route exact path="/" component={Home} />
              <Route path="/experience" component={Experience} />
              <Route path="/projects" component={Projects} />
              <Route path="/game" component={Game} />
              <Route path="/trip-planner" component={Trip} />
            </Switch>
          </div>
        </CSSTransition>
      </TransitionGroup>
    );
  }
}

export default withRouter(App);
