import React, { Component } from 'react';
import { Card, CardBody, CardImg, CardTitle,
         Container, Row, Col,
         Nav, NavItem, NavLink,
         UncontrolledTooltip, UncontrolledCarousel,
         Modal
        } from 'reactstrap'

import { IconContext } from 'react-icons';
import { FaChevronCircleRight as Closed, FaFilePdf as Resume } from 'react-icons/fa'

import { IoMdEye as Preview, IoLogoGithub as Github } from 'react-icons/io'
import { FaArchive } from 'react-icons/fa';


import {web, visual, linguistic} from '../resources/projects.js'
import './styles/Project.scss'

class Projects extends Component {
  constructor(props){
    super(props);
    this.state = {
      web: false
    }
    this.toggle= this.toggle.bind(this);
  }

  toggle(block){
    let state = this.state;
    state[block] = !state[block];
    this.setState(state);
  }

  header(text, toggle) {
    return(
      <div className="sectionHeader" onClick={()=> toggle!=="resume" && this.toggle(toggle)}>
        {toggle!=="resume" ? <Closed className={"show " + (this.state[toggle] ? "open" : "")}/> : <Resume className="show"/>}
        {text}
      </div>
    );
  }
  // softwareBadge(name, icon, placement){
  //   return (
  //     <div>
  //         <UncontrolledTooltip 
  //           placement={placement} 
  //           target={name.replace("++", "pp")
  //                       .replace("HTML", "HTML5")
  //                       .replace(" ", "")}
  //         >
  //           {name}
  //         </UncontrolledTooltip>
  //         {icon}
  //     </div>
  //   );
  // }

  projectCard(project) {
    return(
      <div className="project_card">  
        <UncontrolledCarousel items={project.images} autoPlay={false} indicators={false} controls={project.images.length>1}/>
        <div className="info">
          <b>{project.title}</b>
          {project.description}
          <div className="icons">
            <a href={project.github} target="_blank" rel="noopener noreferrer"><Github/></a>
            {(project.paper !== undefined) &&
              <a href={project.paper} target="_blank" rel="noopener noreferrer"><FaArchive style={{fontSize:28}}/></a>}
            {(project.preview !== undefined) &&
              <a href={project.preview} target="_blank" rel="noopener noreferrer"><Preview/></a>}
          </div>
        </div>
      </div>
    )
  }

  render() {
    return (
      <Container fluid className='page'>
        {this.header("Web Development", "web")}
        <div className= {"hidden " + (this.state.web && "open")} >
          web dev
        </div>
        
        {web.map((project, index) => {
          this.projectCard(project)
        })}  
      </Container>
    )
  }
}
export default Projects;
