import React, {Component} from 'react';
import {Container} from 'reactstrap';
import { TabContent, TabPane, Nav, NavItem, NavLink, Row, Col } from 'reactstrap';
import classnames from 'classnames';
import Options from './Options';
import Plan from './Plan';
import {IconContext} from 'react-icons';
import {IoIosCalculator} from 'react-icons/io';
import {MdPeople, MdFlightTakeoff} from 'react-icons/md';
import {GoGear} from 'react-icons/go';

import Calculator from './Calculator';
import {get_config} from '../../api/api';

/* Renders the application.
 * Holds the destinations and options state shared with the trip.
 */
class Application extends Component {
    constructor(props) {
        super(props);
        this.state = {
            config: {
                "type"          : "config",
                "version"       : 4,
                "units"         : ["kilometers", "miles", "nautical miles", "user defined"],
                "optimization"  : [{"label":"none", "description":"The trip is not optimized."},
                                   {"label":"short", "description":"Nearest neighbor."},
                                   {"label":"shorter", "description":"2-opt."},
                                   {"label":"shortest", "description":"3-opt."}
                ],
                "attributes"    : ["name", "id", "latitude", "longitude"],
                "filters"       : [{"name":"type",
                                    "values":["balloonport", "heliport", "airport", "seaplane base"]},
                                    {"name": "country",
                                     "values": ["Costa Rica", "Japan", "Kenya", "New Zealand", "Spain", "United States"]},
                                    {"name": "continents", 
                                     "values": ["Africa", "Antarctica", "Asia", "Europe",
                                     "Oceania", "North America", "South America"]}
                ],
                "maps"          : ["svg", "kml"]
              },
            distance: {
                type: "distance",
                version: 4,
                origin: {
                    latitude: 0,
                    longitude: 0
                },
                destination: {
                    latitude: 0,
                    longitude: 0
                },
                units: "miles",
                distance: 0
            },
            trip: {
                type: "trip",
                version: 4,
                title: "My Trip",
                options: {
                    units: "miles",
                    unitName: "",
                    unitRadius: 0,
                    optimization: "none",
                    map: "kml"
                },
                places: [{"id":"argn","name":"Argentina","latitude":-38.4161,"longitude":-63.6167},
                {"id":"cair","name":"Cairo","latitude":30.0444,"longitude":31.2357},
                {"id":"auck","name":"Auckland","latitude":-36.8485,"longitude":174.7633},
                {"id":"foco","name":"Fort Collins","latitude":40.585258,"longitude":-105.084419}],
                distances: [7712.3, 10569.52, 7367.52, 6053.56],
                map: null
            },
            port: window.location.port,
            host: window.location.host,
            activeTab: '1',
            realTime: false
        };
        this.updateTrip = this.updateTrip.bind(this);
        this.updateBasedOnResponse = this.updateBasedOnResponse.bind(this);
        this.updateOptions = this.updateOptions.bind(this);
        this.updateHostAndPort = this.updateHostAndPort.bind(this);
        this.updatePlaces = this.updatePlaces.bind(this);
        this.updateOriginAndDestination = this.updateOriginAndDestination.bind(this);
        this.updateDistanceBasedOnResponse = this.updateDistanceBasedOnResponse.bind(this);
        this.toggleTab = this.toggleTab.bind(this);
        this.updateRealTime = this.updateRealTime.bind(this);
    }

    componentWillMount() {

        // get_config().then(
        //     config => {
        //         this.setState({
        //             config: config
        //         })
        //     }
        // );
    }

    updateHostAndPort(host, port) {
        port = port ? port : 0;
        this.setState({port: parseInt(port, 10)});
        host = host ? host : "black-bottle.cs.colostate.edu";
        this.setState({host: host});

        // get_config(this.port, this.host).then(
        //     config => {
        //         this.setState({
        //             config: config
        //         })
        //     }
        // );
    }

    updateDistanceBasedOnResponse(value) {
        this.setState({distance: value});
    }

    updateOriginAndDestination(lat_f, long_f, lat_t, long_t) {
        let distance = this.state.distance;
        distance.origin = {latitude: lat_f, longtitude: long_f};
        distance.destination = {latitude: lat_t, longtitude: long_t};
        this.setState(distance);
    }

    updateTrip(field, value) {
        let trip = this.state.trip;
        trip[field] = value;
        this.setState(trip);
    }

    updateBasedOnResponse(value) {
        this.setState({trip: value});
    }

    updateOptions(option, value) {
        let trip = this.state.trip;
        trip.options[option] = value;
        this.setState(trip);
    }

    //key can only be {"add", "remove", "reverse", "origin"}, and perform accordingly
    updatePlaces(value, key) {
        if (key === "add") {
            this.addPlace(value);
        }
        else if (key === "remove") {
            this.removePlace(value);
        }
        else if  (key === "reverse") {
            this.reversePlaces();
        }
        else if  (key === "origin") {
            this.setFirstPlace(value);
        }
    }

    updateRealTime(value) {
        value = (value === 'true');
        this.setState({realTime: value});
    }

    toggleTab(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }

    addPlace(value) {
        if (this.state.trip.places.length === 0) {
            this.state.trip.places = [value];
        }
        else {
            const place = JSON.stringify(value);
            let found = this.state.trip.places.findIndex(function(ele){
                return JSON.stringify(ele) === place;
            });
            if (found === -1)  {
                this.state.trip.places.push(value);
            }
        }
    }

    removePlace(value){
        const place = JSON.stringify(value);
        let trip = this.state.trip;
        if (this.state.trip.places.length !== 0) {
            trip["places"] = trip["places"].filter(function(ele){
                return JSON.stringify(ele) !== place;
            });
        }
        this.setState(trip);
    }

    reversePlaces() {
        let trip = this.state.trip;
        trip.places.reverse();
        trip.distances.reverse();
        this.setState(trip);
    }

    setFirstPlace(index) {
        let places = [];
        let distances = [];
        for (let i = 0; i < this.state.trip.places.length; i++) {
            places.push(this.state.trip.places[index]);
            distances.push(this.state.trip.distances[index]);
            index = (index + 1) % this.state.trip.places.length;
        }
        let trip = this.state.trip;
        trip.places = places;
        trip.distances = distances;
        this.setState(trip);
    }

    render() {
        if (!this.state.config) {
            return <div/>
        }
        const style ={textDecoration: 'none'};
        const aStyle ={color: '#255f35', textDecoration: 'none'};
        return (
            <React.Fragment>
                <IconContext.Provider value={{ size: '2.4em' }}>
                <Container>
                    <Nav tabs>
                        <Col xs ='5'><h4 style={{'position': 'absolute'}}><b>Trip Planner Demo (T23, CS314)</b></h4></Col>
                        <Col md ='7'>
                            <Row className={'float-right'}>
                                <NavItem>
                                    <NavLink
                                        className={classnames({ active: this.state.activeTab === '1' })}
                                        onClick={() => { this.toggleTab('1');}}
                                        style={(this.state.activeTab === '1')? aStyle : style}
                                    >
                                        <MdFlightTakeoff/>
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink
                                        className={classnames({ active: this.state.activeTab === '2' })}
                                        onClick={() => { this.toggleTab('2'); }}
                                        style={(this.state.activeTab === '2')? aStyle : style}
                                    >
                                        <IoIosCalculator/>
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink
                                        className={classnames({ active: this.state.activeTab === '3' })}
                                        onClick={() => { this.toggleTab('3'); }}
                                        style={(this.state.activeTab === '3')? aStyle : style}
                                    >
                                        <GoGear/>
                                    </NavLink>
                                </NavItem>
                            </Row>
                        </Col>
                    </Nav>
                </Container>
            </IconContext.Provider>
                <TabContent activeTab={this.state.activeTab}>
                    <TabPane tabId="1">
                        <Plan
                            updateBasedOnResponse={this.updateBasedOnResponse}
                            updatePlaces={this.updatePlaces}
                            updateTrip={this.updateTrip}
                            config={this.state.config}
                            trip={this.state.trip}
                            places={this.state.places}
                            port={this.port}
                            host={this.host}
                            realTime = {this.state.realTime}
                        />
                    </TabPane>
                    <TabPane tabId="2">
                        <Calculator
                            updateDistanceBasedOnResponse={this.updateDistanceBasedOnResponse}
                            updateOriginAndDestination={this.updateOriginAndDestination}
                            distance={this.state.distance}
                        />
                    </TabPane>
                    <TabPane tabId="3">
                        <Options
                            options={this.state.trip.options}
                            config={this.state.config}
                            updateOptions={this.updateOptions}
                            updateHostAndPort={this.updateHostAndPort}
                            updateRealTime={this.updateRealTime}
                            realTime = {this.state.realTime}
                        />
                    </TabPane>
                </TabContent>
            </React.Fragment>
        )
    }
}

export default Application;
