import React, { Component } from 'react';
import './Hex.scss';

class HexGrid extends Component {

    hexGrid(h, w) {
        let grid = []
        let color=0x1a44b8;
        for(let i=0; i<h; i++){
            let row = []
            for (let j=0; j<w; j++){
                color+=512;
                row.push(
                    <li>
                        <div className="hexagon" style={{"background":"#"+color.toString(16)}}></div>
                    </li>
                )
            }
            grid.push(
                <div className="row" id={"row_"+i}>
                    {row}
                </div>
            )
        }
        return grid
    }
    render() {
        return (
            <ul id="grid" className="clear">
                {this.hexGrid(30,40)}
            </ul>
        );
    }
} 
export default HexGrid;
